import React,{useState,useEffect} from 'react';
import Header from './ReusableComponents/Header';
import Footer from './ReusableComponents/Footer';
import Grid from '@mui/material/Grid';
import jharkhand from '../assets/images/jharkhand.jpeg';
import cart from '../assets/images/icons8-cart-50.png';
import TextField from '@mui/material/TextField';
import lantern from '../assets/images/dlantern.jpeg';
import pos from '../assets/images/posion.jpeg';

const Cart = () => {
    const [entry,setEntry]=useState('');
    const [plant,setplant]=useState('');
    const [loc,setloc]=useState('');
    var nm=JSON.parse(sessionStorage.getItem("userid")); 
    console.log("nm",nm)
    useEffect(()=>{
        const requestOptions = {
            method: 'GET',
            headers: {'Content-Type': 'application/json'}, 
            body: JSON.stringify()
        };
        fetch(`http://127.0.0.1:8000/api/displaycart/${nm.user.id}`)
        .then((res) => res.json())
        .then((json) => setEntry(json))

        fetch("http://127.0.0.1:8000/api/plantdeetdisplay")
        .then((res) => res.json())
        .then((json) => setplant(json))

        fetch("http://127.0.0.1:8000/api/displaylocwithplant")
        .then((res) => res.json())
        .then((json) => setloc(json))

    },[]) 
    console.log("mm",entry)
    console.log("mm2",plant)
    console.log("mm3",loc)

    let newloc=[]
    if (loc?.result?.length>0){
        loc?.result?.map((list)=>
        {
            if(list.plant_detail){
                newloc.push({
                    ...list
                })
            }
        }
        )
    }
    console.log("newloc",newloc)







    function handleDelete(deetid){
        console.log("deetid",deetid)
        const requestOptions = {
            method: 'DELETE',
            headers: { 'Accept': 'application/json' ,'Content-Type': 'application/json'}
        };

        fetch(`http://127.0.0.1:8000/api/cart/delete/${deetid}`, requestOptions)
            .then(response => response.json())
            .then(data => console.log("dataa",data));


            const requestOptions2 = {
                method: 'GET',
                headers: {'Content-Type': 'application/json'}, 
                body: JSON.stringify()
            };
            fetch("http://127.0.0.1:8000/api/displaylocwithplant")
        .then((res) => res.json())
        .then((json) => setloc(json))
    }
    function handleDelete2(deetid){
        const requestOptions = {
            method: 'DELETE',
            headers: { 'Accept': 'application/json' ,'Content-Type': 'application/json'}
        };
        fetch(`http://127.0.0.1:8000/api/plantdetail/delete/${deetid}`, requestOptions)
            .then(response => response.json())
            .then(data => console.log("dataa2",data));
    }
  return (
    <div>
        <Header/>

            <Grid container spacing={0} className='choice1' style={{padding:"30px"}}>

                <Grid item xs={7} className='cartgrid' style={{marginRight:"20px"}}>
                {newloc?.map((list) => 
                (
                        <Grid container spacing={2} className='choice1' style={{backgroundColor:"white"}}>
                            <Grid item xs={12}>
                            <div className='choice1' style={{justifyContent:"left",backgroundColor:"white"}}>
                                <img src={cart} style={{padding:"20px"}}></img>
                                <h5 style={{color:"red"}}>Just few trees more and you will be eligible for a goody bag.</h5>
                            </div>
                            </Grid>

                            {/* <Grid item xs={3} className='choice1'style={{padding:"20px"}}>
                                <img src={list.imgpath} style={{height:"100px",width:"100px"}}></img>
                            </Grid> */}

                            <Grid item xs={8} style={{padding:"20px"}}>
                                <h2 id='h2cart'>{list?.title}</h2>
                                <p id='pmoneycart'><b>Rs {list.cost}.00</b></p>
                            </Grid>

                            <Grid item xs={3} style={{padding:"20px"}}>
                                <p>Total: Rs {list.cost}.00</p>
                                <button onClick={()=>handleDelete(list.plant_detail.id)} className='loginbutton'>Delete</button>
                            </Grid>

                        </Grid>
                ))}



                        <Grid container spacing={2} className='choice1' style={{backgroundColor:"white"}}>
                            <Grid item xs={9} style={{padding:"20px",display:"flex",alignItems:"center",justifyContent:"left"}}>
                            <TextField id="outlined-basic" label="Outlined" variant="outlined" />
                            <button className='cartbuttons'>Apply ECODE</button>
                            </Grid>

                            <Grid item xs={3} style={{padding:"20px"}}>
                                <button className='cartbuttons'>Add more trees</button>
                            </Grid>
                        </Grid>

                </Grid>

                <Grid item xs={4} className='cartgrid' style={{padding:"10px",marginLeft:"20px",backgroundColor:"white"}}>
                    <h2>
                        Add-ons to support farmers
                    </h2>


                    <div style={{textAlign:"center"}}>
                        <h3>D-lantern</h3>
                        <img src={lantern}></img>
                        <p>
                        Support our farmer beneficiaries to work more efficiently and improve their quality of life by donating solar lanterns to them.
                        </p>
                    </div>
                   <Grid container spacing={0} className='choice1'>
                    <Grid item xs={6} className='choice1'>
                        <p>
                            Price: Rs 949.00
                        </p>
                    </Grid>
                    <Grid item xs={6} className='choice1'>
                        <buttom className='cartbuttons'>Add to cart</buttom>
                    </Grid>
                   </Grid>
                   <hr/>


                    <div style={{textAlign:"center"}}>
                        <h3>Poshan Kit</h3>
                        <img src={pos} style={{width:"100%"}}></img>
                        <p>
                        Donate a Poshan kit to our women farmers to fight against anemia. Nutritious Poshan kit is rich in Iron, Protein, Omega 3, 6 fatty acids and fiber sourced from nature and prepared without harmful preservatives.                        </p>
                    </div>
                   <Grid container spacing={0} className='choice1'>
                    <Grid item xs={6} className='choice1'>
                        <p>
                            Price: Rs 399.00
                        </p>
                    </Grid>
                    <Grid item xs={6} className='choice1'>
                        <buttom className='cartbuttons'>Add to cart</buttom>
                    </Grid>
                   </Grid>
                   <hr/>


                </Grid>
            </Grid>

        <Footer/>
    </div>
  )
}

export default Cart
