import React from 'react';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import logo from '../assets/images/Untitled design (1).png'
import bg from '../assets/images/loginbg-removebg-preview.png'
import { alpha, styled } from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import TextField from '@mui/material/TextField';
import FormControl from '@mui/material/FormControl';

const CssTextField = styled(TextField)({
    '& label.Mui-focused': {
      color: '#A0AAB4',
    },
    '& .MuiInput-underline:after': {
      borderBottomColor: '#B2BAC2',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: '#E0E3E7',
      },
      '&:hover fieldset': {
        borderColor: '#B2BAC2',
      },
      '&.Mui-focused fieldset': {
        borderColor: '#6F7E8C',
      },
    },
  });

  const BootstrapInput = styled(InputBase)(({ theme }) => ({
    'label + &': {
      marginTop: theme.spacing(3),
    },
    '& .MuiInputBase-input': {
      borderRadius: 4,
      position: 'relative',
      backgroundColor: theme.palette.mode === 'light' ? '#F3F6F9' : '#1A2027',
      border: '1px solid',
      borderColor: theme.palette.mode === 'light' ? '#E0E3E7' : '#2D3843',
      fontSize: 16,
      width: 'auto',
      padding: '10px 12px',
      transition: theme.transitions.create([
        'border-color',
        'background-color',
        'box-shadow',
      ]),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(','),
      '&:focus': {
        boxShadow: `${alpha(theme.palette.primary.main, 0.25)} 0 0 0 0.2rem`,
        borderColor: theme.palette.primary.main,
      },
    },
  }));



const ValidationTextField = styled(TextField)({
  '& input:valid + fieldset': {
    borderColor: '#E0E3E7',
    borderWidth: 1,
  },
  '& input:invalid + fieldset': {
    borderColor: 'red',
    borderWidth: 1,
  },
  '& input:valid:focus + fieldset': {
    borderLeftWidth: 4,
    padding: '4px !important', // override inline-style
  },
});

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(5),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));



const Login = () => {
  return (
    <div>
    <div>
        <Grid container spacing={2} className='choice1' style={{padding:"40px"}}>
        <Grid item xs={4}>
          <Item  className='loginitem' style={{backgroundImage:`url(${bg})`}}>
            <img src={logo} style={{height:"70px"}}></img>
            <div className='choice1'>
                <h4>Login</h4>
                <h4>Sign Up</h4>
            </div>
            <Box component="form" noValidate sx={{display: 'grid', margin:'20px',
                                                gridTemplateColumns: { sm: '1fr 1fr' },
                                                gap: 2}}>
                <FormControl variant="standard">
                    <BootstrapInput defaultValue="Enter email" id="bootstrap-input" />
                </FormControl>

            </Box>
            <Box component="form" noValidate sx={{display: 'grid', margin:'20px',
                                                gridTemplateColumns: { sm: '1fr 1fr' },
                                                gap: 2}}>
                <FormControl variant="standard">
                    <BootstrapInput defaultValue="Enter email" id="bootstrap-input" />
                </FormControl>

            </Box>
          </Item>
        </Grid>

      </Grid>
    </div>
    </div>
  )
}

export default Login
